const express = require("express");
const cron = require("node-cron");

const { ENV } = require("./src/config/env");
const { JWT } = require("google-auth-library");
const { GoogleSpreadsheet } = require("google-spreadsheet");
const { ID_TABLE, NUMBER_SHEETS } = require("./src/constants");
const { processMatchingChangeBudget } = require("./src/utils");
const { Puppeter } = require("./src/service/Puppeter");
const app = express();

const serviceAccountAuth = new JWT({
  // env var values here are copied from service account credentials generated by google
  // see "Authentication" section in docs for more info
  email: ENV.EMAIL,
  key: ENV.PASSWORD,
  scopes: ["https://www.googleapis.com/auth/spreadsheets"],
});

const doc = new GoogleSpreadsheet(ID_TABLE, serviceAccountAuth);
const port = process.env.PORT || 3001;

app.get("/", (req, res) => {
  res.send("Parser work").status(200);
});

app.listen(port, async () => {
  console.log(`Server listening on port ${port}`);

  try {
    await doc.loadInfo();

    cron.schedule("30 * * * *", async () => {
      console.log("running a task every hour in 30 min for test");
    });

    const puppeter = await Puppeter.init();
    console.log("Puppeteer init =========>", 99999999);

    // // проверяет предыдущие записанные матчи и изменяет бюджет ровно в 9:00 по МСК.
    // // 06:00 по серверу.
    cron.schedule("00 06 * * *", async () => {
      console.log("running a task every day in 09:00 +3 hour by Moscow");
      await processMatchingChangeBudget(
        NUMBER_SHEETS.SECOND_SHEET,
        doc,
        puppeter,
        { isChangeBudget: true }
      );

      await processMatchingChangeBudget(
        NUMBER_SHEETS.THIRD_SHEET,
        doc,
        puppeter,
        { isChangeBudget: true }
      );

      await processMatchingChangeBudget(
        NUMBER_SHEETS.FOURTH_SHEET,
        doc,
        puppeter,
        { isChangeBudget: true }
      );
    });

    // // Добавляет в таблицу список матчей ровно в 14:00 по МСК. 11:00 по серверу.
    // // Если в таблицу уже были добавлены, то не добавляет
    cron.schedule("00 11 * * *", async () => {
      console.log("running a task every day in 14:00 +3 hour by Moscow");
      await processMatchingChangeBudget(
        NUMBER_SHEETS.SECOND_SHEET,
        doc,
        puppeter,
        { isAddMatches: true }
      );

      await processMatchingChangeBudget(
        NUMBER_SHEETS.THIRD_SHEET,
        doc,
        puppeter,
        { isAddMatches: true }
      );

      await processMatchingChangeBudget(
        NUMBER_SHEETS.FOURTH_SHEET,
        doc,
        puppeter,
        { isAddMatches: true }
      );
    });
  } catch (error) {
    console.log("Error index", error);
  }
});
